package basicProgram;

// program to find range of integer, byte, short, long date types
public class Range {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//range of byte data type
		System.out.println(" Byte data types");
		System.out.println("min value = " + Byte.MIN_VALUE);
		System.out.println("max value = " + Byte.MAX_VALUE);
		
		//range of short data type
		System.out.println(" Short data types");
		System.out.println("min value = " + Short.MIN_VALUE);
		System.out.println("max value = " + Short.MAX_VALUE);
		
		//range of long data type
		System.out.println(" Long data types");
		System.out.println("min value = " + Long.MIN_VALUE);
		System.out.println("max value = " + Long.MAX_VALUE);
		
		//range of integer data type
		System.out.println(" Integer data types");
		System.out.println("min value = " + Integer.MIN_VALUE);
		System.out.println("max value = " + Integer.MAX_VALUE);
				
		
		//range of double data type
		System.out.println(" Double data types");
		System.out.println("min value = " + Double.MIN_VALUE);
		System.out.println("max value = " + Double.MAX_VALUE);

	}

}
